import React, { useEffect, useState } from "react";
import Doctor from "../Assets/img2.png";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faCamera, faAngleUp } from "@fortawesome/free-solid-svg-icons";
import { useNavigate  } from "react-router-dom";
import "../Styles/Hero.css";

function Hero() {
  const navigate = useNavigate();
  const [goUp, setGoUp] = useState(false);

  const scrollToTop = () => {
    window.scrollTo({ top: 0, behavior: "smooth" });
  };

  const handleBookAppointmentClick = () => {
    navigate("/appointment");
  };

  useEffect(() => {
    const onPageScroll = () => {
      if (window.scrollY > 600) {
        setGoUp(true);
      } else {
        setGoUp(false);
      }
    };
    window.addEventListener("scroll", onPageScroll);

    return () => {
      window.removeEventListener("scroll", onPageScroll);
    };
  }, []);

  return (
    <div className="section-container">
      <div className="hero-section">
        <div className="text-section">
          <p className="text-headline">üéôÔ∏èMaster your interviews</p>
          <h2 className="text-title">
          Practice with interviews tailored to your needs on our placement preparation website.
          </h2>
          <p className="text-descritpion">
          Receive personalized feedback and insights to enhance your performance.
          Access anytime, anywhere ‚Äì 24/7 availability for your convenience.
          Unlock your full potential and ace your next interview
          </p>
          <button
            className="text-appointment-btn"
            type="button"
            onClick={handleBookAppointmentClick}
          >
            <FontAwesomeIcon icon={faCamera} /> Get Started
          </button>
          <div className="text-stats">
            <div className="text-stats-container">
              <p>145k+</p>
              <p>Users</p>
            </div>

            <div className="text-stats-container">
              <p>50+</p>
              <p>Expert Mentors</p>
            </div>

            <div className="text-stats-container">
              <p>5+</p>
              <p>Years of Excellence</p>
            </div>
          </div>
        </div>

        <div className="hero-image-section">
          <img className="hero-image1" src={Doctor} alt="Doctor" />
        </div>
      </div>

      <div
        onClick={scrollToTop}
        className={`scroll-up ${goUp ? "show-scroll" : ""}`}
      >
        <FontAwesomeIcon icon={faAngleUp} />
      </div>
    </div>
  );
}

export default Hero;
